import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Axios from "axios";
import CityComponent from "./modules/CityComponent";
import WeatherComponent from "./modules/WeatherInfoComponent";

export const WeatherIcons = {
  "01d": "/icons/sunny.svg",
  "01n": "/icons/night.svg",
  "02d": "/icons/day.svg",
  "02n": "/icons/cloudy-night.svg",
  "03d": "/icons/cloudy.svg",
  "03n": "/icons/cloudy.svg",
  "04d": "/icons/perfect-day.svg",
  "04n": "/icons/cloudy-night.svg",
  "09d": "/icons/rain.svg",
  "09n": "/icons/rain-night.svg",
  "10d": "/icons/rain.svg",
  "10n": "/icons/rain-night.svg",
  "11d": "/icons/storm.svg",
  "11n": "/icons/storm.svg",
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 380px;
  height: 620px; //820px on figma
  padding: 20px 10px;
  margin: auto;
  border-radius: 4px;
  box-shadow: 0 3px 6px 0 #555;
  background: #13131a;
`;

const CloseButton = styled.span`
  padding: 2px 3px;
  background-color: black;
  border-radius: 50%;
  color: white;
  position: absolute;
`;

function App() {
  const [weather, updateWeather] = useState();

  const fetchWeatherByLocation = async () => {
    try {
      // Konum izni iste
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });
      const { latitude, longitude } = position.coords;
      // Koordinatları kullanarak hava durumu bilgisi al
      const response = await Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=8297725875f39e37ed6a7bc2e1fc738c`
      );
      updateWeather(response.data);
    } catch (error) {
      console.error("Konum bilgisini alırken bir hata oluştu:", error);
    }
  };

  const fetchWeatherByCity = async (city) => {
    try {
      const response = await Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=8297725875f39e37ed6a7bc2e1fc738c`
      );
      updateWeather(response.data);
    } catch (error) {
      console.error("Hava durumu bilgisini alırken bir hata oluştu:", error);
    }
  };

  return (
    <Container>
      {weather ? (
        <WeatherComponent weather={weather} />
      ) : (
        <>
          <button onClick={fetchWeatherByLocation}>
            Konuma Göre Hava Durumu Getir
          </button>
          <CityComponent fetchWeather={fetchWeatherByCity} />
        </>
      )}
    </Container>
  );
}

export default App;
