import React from "react";
import styled from "styled-components";
import { WeatherIcons } from "../App";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

export const WeatherInfoIcons = {
  Sunset: "/icons/temp.svg",
  Sunrise: "/icons/temp.svg",
  Humidity: "/icons/humidity.svg",
  Wind: "/icons/wind.svg",
  Pressure: "/icons/pressure.svg",
  Thermal_Sensation: "/icons/thermometer.svg",
};
const Location = styled.span`
  margin: 15px auto;
  text-transform: capitalize;
  font-size: 28px;
  font-weight: bold;
  color: White;
`;
const Condition = styled.span`
  margin: 20px auto;
  text-transform: capitalize;
  font-size: 14px;
  color: #ffffff;

  & span {
    width: 97px;
    height: 58px;

    /* Heading xl */
    font-family: "Nunito";
    font-style: normal;
    font-weight: 800;
    font-size: 48px;
    line-height: 120%;
    /* identical to box height, or 58px */
    display: flex;
    align-items: center;
    text-align: right;

    /* Base/White */
    color: #ffffff;
  }
`;

const WeatherIcon = styled.img`
  width: 100px;
  height: 100px;
  margin: 5px auto;
`;
const WeatherContainer = styled.div`
  display: flex;
  width: 100%;
  margin: 30px auto;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  background-image: ${({ weather }) => {
    const isDay = isDaytime(weather?.weather[0].icon);
    const weatherCondition = weather?.weather[0].main;
    let imageUrl;

    if (isDay) {
      imageUrl = getDaytimeImage(weatherCondition);
    } else {
      imageUrl = getNighttimeImage(weatherCondition);
    }

    return `url('${imageUrl}')`;
  }};
  background-size: cover;
  background-repeat: no-repeat;
`;

// Gece için arka plan resmi
const getNighttimeImage = (weatherCondition) => {
  switch (weatherCondition) {
    case "Clear":
      return "public/bg/Weather=Clear, Moment=Night.png";
    case "Clouds":
      return "public/bg/Weather=Cloudy, Moment=Night.png";
    case "Rain":
      return "/images/rainy_night.jpg";
    default:
      return "/images/default_night.jpg";
  }
};

// Saatin gündüz veya gece olup olmadığını kontrol et
const isDaytime = (icon) => {
  const hour = new Date().getHours();
  return hour >= 6 && hour < 18; // Gündüz saatleri 6:00-18:00 arasındadır
};

const WeatherInfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 4px 16px;
  gap: 8px;
  height: 292px;
`;

const InfoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  gap: 24px;

  width: 327px;
  height: 56px;
  border-bottom: 1px solid #1c1c27;
  background: #16161f;
  border-radius: 12px;
`;
const InfoIcon = styled.img`
  width: 36px;
  height: 36px;
`;
const InfoLabel = styled.span`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  gap: 24px;

  width: 327px;
  height: 56px;
  color: #fafafa;
  & span {
    //styleName: Heading xs;
    font-family: Nunito;
    font-size: 14px;
    font-weight: 700;
    line-height: 19.6px;
    text-align: center;
    color: #bfbfd4;
  }
`;

const WeatherInfoComponent = (props) => {
  const { name, value } = props;
  return (
    <InfoContainer>
      <InfoIcon src={WeatherInfoIcons[name]} />
      <InfoLabel>
        <span>{name}</span>
        {value}
      </InfoLabel>
    </InfoContainer>
  );
};

const BackButton = styled.span`
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
  color: white;
  background-color: #7692c9;
  padding: 5px 10px;
  border-radius: 5px;
`;

const WeatherComponent = (props) => {
  const { weather, onBackButtonClick } = props;
  const isDay = weather?.weather[0].icon?.includes("d");
  const getTime = (timeStamp) => {
    return `${new Date(timeStamp * 1000).getHours()} : ${new Date(
      timeStamp * 1000
    ).getMinutes()}`;
  };
  return (
    <>
      <WeatherContainer weather={weather}>
        <Condition>
          <span>{`${Math.floor(weather?.main?.temp - 273)}°C`}</span>
          {`  |  ${weather?.weather[0].description}`}
        </Condition>
        <WeatherIcon src={WeatherIcons[weather?.weather[0].icon]} />
      </WeatherContainer>
      <Location>{`${weather?.name}, ${weather?.sys?.country}`}</Location>

      <WeatherInfoContainer>
        <WeatherInfoComponent
          name={"Thermal_Sensation"}
          value={`${Math.floor(weather?.main?.feels_like - 273)}°C`}
        />
        <WeatherInfoComponent
          name={isDay ? "Sunset" : "Sunrise"}
          value={`${getTime(weather?.sys[isDay ? "sunset" : "sunrise"])}`}
        />
        <WeatherInfoComponent
          name={"Humidity"}
          value={weather?.main?.humidity}
        />
        <WeatherInfoComponent name={"Wind"} value={weather?.wind?.speed} />
        <WeatherInfoComponent
          name={"Pressure"}
          value={weather?.main?.pressure}
        />
      </WeatherInfoContainer>
      <BackButton onClick={onBackButtonClick}>
        <FontAwesomeIcon icon={faArrowLeft} />
      </BackButton>
    </>
  );
};

export default WeatherComponent;
